package com.xeon.javaclash.protocol.messages.server.battle;

import com.xeon.javaclash.core.Connection;
import com.xeon.javaclash.protocol.messages.PiranhaMessage;

public class NpcDataMessage extends PiranhaMessage {
    public NpcDataMessage(Connection connection){
        super(connection);
        this.id = 24346;
    }

    @Override
    public void encode() {
        writer.writeHexa("");
        writer.writeHexa("");
        writer.writeHexa("");
        this.connection.player.LogicClientAvatar(writer);
        writer.writeInt(1);
        writer.writeInt(0);
        writer.writeInt(17000075); //Npc ID
        writer.writeByte(0);
    }

}
